// helpers.js
export const getBackgroundColor = (category) => {
  const cat = Number(category);
  switch (cat) {
    case 0:
      return '#006400';
    case 1:
      return '#228B22'; 
    case 2:
      return '#32CD32'; 
    default:
      return '#000000'; 
  }
};
