// App.js
import React, { useState } from 'react';
import axios from 'axios';
import './App.css'; // Import the CSS file

function App() {
  const [word, setWord] = useState('');
  const [rhymes, setRhymes] = useState([]);

  const fetchRhymes = async () => {
    try {
      const response = await axios.get(`http://127.0.0.1:8000/rhyme/${word}`);
      setRhymes(response.data.rhymes);
    } catch (error) {
      console.error('Error fetching rhymes:', error);
    }
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      fetchRhymes();
    }
  };
  return (
    <div className='app-container'>
      <h1 className='title'>RihƒçaRiibma</h1>
      <input
        type='text'
        value={word}
        onChange={(e) => setWord(e.target.value)}
        onKeyDown={handleKeyPress}
        placeholder='Enter a word'
        className='input-field'
      />
      <button onClick={fetchRhymes} className='button'>
        Oza
      </button>

      <h2 className='rhymes-title'></h2>
      <ul>
        {rhymes.map((rhyme, index) => (
          <li key={index} className='rhyme-item'>
            {rhyme}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
