// helpers.js
export const getBackgroundColor = (category) => {
    const cat = Number(category);
    switch (cat) {
      case 0:
        return '#006400'; // DarkGreen
      case 1:
        return '#228B22'; // ForestGreen
      case 2:
        return '#32CD32'; // LimeGreen
      default:
        return '#000000'; // Default or fallback color
    }
  };
