import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
import { getBackgroundColor } from './helpers';

function App() {
  const [word, setWord] = useState('');
  const [rhymesByCategory, setRhymesByCategory] = useState({});

  const fetchRhymes = async () => {
    try {
      const response = await axios.get(`http://127.0.0.1:8000/rhyme/${word}`);
      setRhymesByCategory(response.data.rhymes);
    } catch (error) {
      console.error('Error fetching rhymes:', error);
    }
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      fetchRhymes();
    }
  };

  // Helper function to determine background color based on category key
  const getBackgroundColor = (category) => {
    // Convert the category key to a number if needed
    const cat = Number(category);
    // For example, a scale: the lower the number, the darker the green.
    switch (cat) {
      case 0:
        return '#006400'; // DarkGreen
      case 1:
        return '#228B22'; // ForestGreen
      case 2:
        return '#32CD32'; // LimeGreen
      default:
        return '#000000'; // LightGreen for higher categories
    }
  };

  return (
    <div className='app-container'>
      <h1 className='title'>RihƒçaRiibma</h1>
      <input
        type='text'
        value={word}
        onChange={(e) => setWord(e.target.value)}
        onKeyDown={handleKeyPress}
        placeholder='Enter a word'
        className='input-field'
      />
      <button onClick={fetchRhymes} className='button'>
        Oza
      </button>

      {/* Render each category */}
      {Object.keys(rhymesByCategory).length > 0 && (
        <div className='categories-container'>
          {Object.keys(rhymesByCategory)
            .sort((a, b) => a - b)
            .map((category) => (
              <div
                key={category}
                className='category-container'
                style={{ backgroundColor: getBackgroundColor(category) }}
              >
                <h2 className='category-title'>Category {category}</h2>
                <ul className='rhymes-list'>
                  {rhymesByCategory[category].map((rhyme, index) => (
                    <li key={index} className='rhyme-item'>
                      {rhyme}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
        </div>
      )}
    </div>
  );
}

export default App;
